$.fn.mostInViewport=function(){for(var o,t=0,r=this,e=0;e<r.length;e++){var f=r[e],i=$(f).offset().top,n=i+$(f).outerHeight(),h=$(window).scrollTop(),s=h+$(window).height(),w=0;if(n>h&&i<s){var p=i<=h;(w=p?n-h:s-i)>t?(o=f,t=w):w===t&&p&&i<$(o).offset().top&&(o=f,t=w)}}return o};
